loadCommentsFromServer() {

    let that = this

    return fetch(this.url)
    .then(function(response) {

        return response.json()
    })
    .then(function(data) {

        that.setState({data: data})
    })
    .catch(function(e) {

        console.error(that.props.url,e)
    });
}

componentDidMount() {

    this.loadCommentsFromServer()
}

